'use strict'

const http = require('http')
const AgentBase = require('./agent-base')
const Config = require('../config')
const Logger = require('./../logger')
const retry = require('retry')

/**
 * This class will handle the sending of protobuf messages through HTTP.
 * @constructor
 */
function AgentSocket () {
  // Call the parent constructor
  AgentBase.call(this)
  this._config = new Config()
  this.transactions = null
}

/**
 * This function is responsible in sending of protobuf messages through unix domain socket.
 * @constructor
 */
AgentSocket.prototype.send = function (transactions, cb) {
  try {
    var operation = retry.operation({ retries: this._config.getMaxRetries(), minTimeout: this._config.getDelaySeconds() })
    this.transactions = transactions
    var protobufObj = AgentBase.prototype.build_message(transactions)
    // Convert message into binary and send it to unix domain socket. Serializes to a UInt8Array.
    var serialized = protobufObj.serializeBinary()

    var callback = function callback (res) {
      if (res.statusCode === 200) {
        Logger.debug(`AgentSocket.post - Successfully sent via unix domain socket. StatusCode: ${res.statusCode} StatusMsg: ${res.statusMessage}`)
        cb(null, { 'success': true, 'statusCode': 200 })
      } else {
        Logger.debug(`AgentSocket.post - Failed sending via unix domain socket. StatusCode: ${res.statusCode} StatusMsg: ${res.statusMessage} `)
        let error = { 'success': false, 'statusCode': res.statusCode, 'message': res.statusMessage }
        cb(error)
      }
    }

    var options = {
      socketPath: this._config.socket_path,
      path: this._config.agent_traces_url,
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-protobuf'
      }
    }

    operation.attempt(function (currentAttempt) {
      var client = http.request(options, callback)
      /* set timeout of 5 seconds on request */
      client.on('socket', function (socket) {
        socket.setTimeout(5000)
        socket.on('timeout', function () {
          client.abort()
        })
      })
      client.on('error', function (err) {
        Logger.debug('AgentSocket.post clientError: ' + err)
        return operation.retry(err)
      })
      /* Convert data into binary Buffer */
      var data = Buffer.from(serialized, 'utf8')
      client.write(data)
      client.end()
    })
  } catch (error) {
    Logger.debug('AgentSocket.post error:' + error)
  }
}

/** @class AgentSocket */
module.exports = AgentSocket
