'use strict'

const zlib = require('zlib')

/**
 * This class will handle the sending of transactions to aws lambda.
 * @constructor
 */
function AwsLambdaLogging () {
}

/**
 * Save trace content to console log - synchronously
 * @param content
 */
AwsLambdaLogging.prototype.log = function (content) {
  let str = 'STACKIFY-TRACE: '
  let result = zlib.gzipSync(content)
  let buf = Buffer.from(result)
  let encodedData = buf.toString('base64')
  console.log(str + encodedData)
}

/** @class AwsLambdaLogging */
module.exports = AwsLambdaLogging
